package pattern_programs;

public class Number_pattern {


	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=5; i>=1; i--) {
			
			//System.out.print(i);
			
			for(int j=1; j<=i; j++) {
				
				System.out.print(j);
			}
			
			System.out.println();
		}
	}

}
